sult[\'height\'] = $dims[1];\n	}\n	if (!empty($toPlaceholder)) {\n		$modx->setPlaceholders(array(\n			$toPlaceholder => $result[\'src\'],\n			\"$toPlaceholder.width\" => $result[\'width\'],\n			\"$toPlaceholder.height\" => $result[\'height\']\n		));\n		$output = \'\';\n	}\n	if ($result[\'outputDims\']) {\n		$output = \"src=\\\"{$result[\'src\']}\\\"\" . ($result[\'width\'] ? \" width=\\\"{$result[\'width\']}\\\" height=\\\"{$result[\'height\']}\\\"\" : \'\');\n	}\n}\nelse {\n	$output = $result[\'src\'];\n}\n\nif ($debug && $result[\'success\']) {  // if debugging is on and createThumbnail was successful, log the debug info\n	$pThumb->debugmsg(isset($pThumb->phpThumb->debugmessages) ? \':: Processed ::\' : \":: Loaded from cache: {$result[\'src\']}\", true);\n}\n\nreturn $output;', 0, 'a:1:{s:5:\"debug\";a:7:{s:4:\"name\";s:5:\"debug\";s:4:\"desc\";s:22:\"prop_pthumb.debug_desc\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:18:\"phpthumbof:default\";s:4:\"area\";s:0:\"\";}}', '', 0, ''),
(47, 0, 0, 'StercSeoSiteMap', 'Outputs the sitemap based on the resource settings made in SEO Tab', 0, 17, 0, 'require_once $modx->getOption(\'stercseo.core_path\', null, $modx->getOption(\'core_path\').\'components/stercseo/\').\'model/stercseo/stercseo.class.php\';\n$stercseo       = new StercSeo($modx, $scriptProperties);\n$allowSymlinks  = (isset($allowSymlinks)) ? $allowSymlinks : 0;\n$contexts       = (isset($contexts)) ? explode(\',\',str_replace(\' \', \'\', $contexts)) : array($modx->resource->get(\'context_key\'));\n$outerTpl       = (isset($outerTpl)) ? $outerTpl : \'sitemap/outertpl\';\n$rowTpl         = (isset($rowTpl)) ? $rowTpl : \'sitemap/rowtpl\';\n$type           = (isset($type)) ? $type : \'\';\n$indexOuterTpl  = (isset($indexOuterTpl)) ? $indexOuterTpl : \'sitemap/index/outertpl\';\n$indexRowTpl    = (isset($indexRowTpl)) ? $indexRowTpl : \'sitemap/index/rowtpl\';\n$imagesOuterTpl = (isset($imageOuterTPl)) ? $imagesOuterTpl : \'sitemap/images/outertpl\';\n$imagesRowTpl   = (isset($imagesRowTpl)) ? $imagesRowTpl : \'sitemap/images/rowtpl\';\n$imageTpl       = (isset($imageTpl)) ? $imageTpl : \'sitemap/images/imagetpl\';\n$templates      = (isset($templates)) ? $templates : \'\';\n\n$options = array(\n    \'outerTpl\'       => $outerTpl,\n    \'rowTpl\'         => $rowTpl,\n    \'type\'           => $type,\n    \'indexOuterTpl\'  => $indexOuterTpl,\n    \'indexRowTpl\'    => $indexRowTpl,\n    \'imagesOuterTpl\' => $imagesOuterTpl,\n    \'imagesRowTpl\'   => $imagesRowTpl,\n    \'imageTpl\'       => $imageTpl,\n    \'templates\'      => $templates\n);\n\nreturn $stercseo->sitemap($contexts, $allowSymlinks, $options);', 0, 'a:0:{}', '', 0, ''),
